mapper.select:
select * 
from t_account_bill
where f_uid = #{uid}

	and f_create_time < #{index}

order by f_create_time desc
limit #{size}

select *
from t_account_bill
where f_uid = #{uid}

	
		and f_type = #{type}
	
	
		and f_type in(4,5,19)
	


	and f_in_out = #{status}


	and (f_order_id LIKE CONCAT(CONCAT('%', #{searchfor}), '%') or f_desc LIKE CONCAT(CONCAT('%', #{searchfor}), '%'))

order by f_create_time
desc

	LIMIT #{start},#{size}

select *
from t_account_bill
where 

	f_uid = #{uid}


	and f_type = #{type}


	and f_in_out = #{status}


	and (f_order_id LIKE CONCAT(CONCAT('%', #{searchfor}), '%') or f_desc LIKE CONCAT(CONCAT('%', #{searchfor}), '%'))

order by f_create_time desc

	LIMIT #{start},#{size}

SELECT f.f_uid, f.f_fish_site_id, f.f_fish_site_name, u.f_user_nickname, u.f_user_phonenum, f.f_main_img, a.f_balance, ( SELECT ( CASE WHEN SUM(d.f_balance) IS NULL THEN '0' ELSE SUM(d.f_balance) END ) FROM t_deposit_fish d WHERE f.f_fish_site_id = d.f_fish_site_id ) AS deposits, ( SELECT ( CASE WHEN SUM(d.f_trade_fee) IS NULL THEN '0' ELSE SUM(d.f_trade_fee) END ) FROM t_account_bill d WHERE d.f_type in (5,19) AND d.f_uid = u.f_uid ) AS withdraw_total, ( SELECT ( CASE WHEN SUM(r.f_trade_money) IS NULL THEN '0' ELSE SUM(r.f_trade_money) END ) FROM t_deposit_fish_record r WHERE r.f_direction = '1' AND r.f_fish_site_id = f.f_fish_site_id ) AS depositfish_total FROM t_fish_site f, t_user u, t_account a WHERE f.f_uid = u.f_uid AND u.f_uid = a.f_uid
AND f.f_city_id = #{regionId}
and (f.f_fish_site_name like CONCAT(CONCAT('%', #{searchfor}), '%') or u.f_user_phonenum LIKE CONCAT(CONCAT('%', #{searchfor}), '%'))
ORDER BY f.f_create_time desc
ORDER BY f.f_create_time asc
LIMIT #{start},#{size}
