mapper.select:
SELECT
,if((SELECT COUNT(*) FROM t_focus WHERE f_uid = #{uid} AND f_fid = f_fish_site_id)!=0,1,0) AS f_focus, f_fish_site_name, f_longitude,f_latitude,f_main_img, round(6370996.81 * 2 * asin(sqrt( pow(sin(((#{lat} * PI() /180.0) - (f_latitude * PI() /180.0))/2),2) + cos(#{lat} * PI() /180.0) * cos(f_latitude * PI() /180.0) * pow(sin(((#{lng}-f_longitude) * PI()/180.0)/2),2)))) as f_duration FROM t_article a LEFT JOIN t_fish_site f ON a.f_shop_id = f.f_fish_site_id
a.f_article_status = 2 AND a.f_type = 4 AND f_status = 1 AND f_city_id = #{regionId}
and f_article_id < #{start}
ORDER BY f_article_id DESC LIMIT #{size}

SELECT
	,f_user_nickname,f_user_portriat
FROM
	t_article LEFT JOIN t_user ON f_article_uid = f_uid
	left join  t_fish_site on f_shop_id = f_fish_site_id

	
		and f_article_status = #{status}
	
	
		and f_article_uid = #{uid}
	
	
		and f_city_id = #{regionId}
	
	
		and f_shop_id = #{shopId}
	
	
		and f_article_id <#{start}
	
	
		
			and f_type in (3,4)
		
		
			and f_type = #{type}
		
	

ORDER BY
	f_article_id DESC
LIMIT #{size}

SELECT
	,f_user_nickname,f_user_portriat
FROM
	t_article
	 LEFT JOIN t_user ON f_article_uid = f_uid
	 LEFT JOIN t_fish_shop ON f_shop_id = f_fish_shop_id

	
		and f_article_status = #{status}
	
	
		and f_fish_shop_city_id = #{regionId}
	 
	
		and f_article_id <#{start}
	
	
		
			and f_type in (3,4)
		
		
			and f_type = #{type}
		
	

ORDER BY
	f_article_id DESC
LIMIT #{size}

mapper.insert:
INSERT INTO t_article (
f_article_title, f_article_sub_title, f_article_main_picture, f_article_description, f_article_create_time, f_article_update_time, f_article_status, f_article_content_id, f_article_uid, f_shop_id, f_type, f_article_location, f_article_lng, f_article_lat, f_article_site ) VALUES (
#{title}, #{subTitle}, #{mainPicture}, #{description}, #{createTime}, #{updateTime}, #{status}, #{contentId}, #{uid}, #{shopId}, #{type}, #{location}, #{lng}, #{lat}, #{site}
)

mapper.update:
UPDATE t_article
SET f_article_content_id = #{contentId}
WHERE
	f_article_id = #{id}

UPDATE t_article_content
SET f_content = #{content}
WHERE
	f_content_id = #{contentId}
