mapper.select:
SELECT

FROM t_deposit_fish

	
		and f_uid = #{userId}
	
	
		and f_fish_site_id = #{siteId}
	


	
		ORDER BY ${order} asc
	
	
		ORDER BY ${order} desc
	


	LIMIT #{start},#{size}

mapper.update:
update
t_deposit_fish
set f_balance = #{balance}
where f_uid = #{uid} and
f_fish_site_id = #{fishSiteId}
