mapper.select:
select 

from 
t_fish_post_news
where f_fish_site_id = #{fishSiteId}

	and f_type = #{type}


	and f_nid < #{start}

order by f_nid desc
 limit #{size}

select f_nid,f_picture,f_main_picture,f_content,t1.f_create_time,t1.f_fish_site_id,f_type,f_fish_site_name,t2.f_duration
,f_focus
from t_fish_post_news t1 join (select f_fish_site_id,f_uid, f_partner_id, f_fish_site_name, f_longitude,f_latitude,
f_focus,
round(6370996.81 * 2 * asin(sqrt( pow(sin(((#{lat} * PI() /180.0) - (f_latitude * PI() /180.0))/2),2) + cos(#{lat} * PI() /180.0) * cos(f_latitude * PI() /180.0) * pow(sin(((#{lng}-f_longitude) * PI()/180.0)/2),2)))) as f_duration from
t_fish_site t1 left join (select 1 as f_focus,f_fid from t_focus where f_uid = #{uid}) t2 on t1.f_fish_site_id = t2.f_fid
t_fish_site
where f_status = 1 and f_city_id = #{regionId} )t2 on t1.f_fish_site_id = t2.f_fish_site_id
and t1.f_nid < #{start}
order by t1.f_nid desc limit #{size}
