mapper.select:
select
	 
from t_reward_withdraw_orders
where f_business_id = #{bid} and f_business_type = #{type}

	and id < #{start}
	
order by f_trade_no desc
limit #{size}

		
		select 
    	from (
	        select 
	         	
	        , if(date_add(f_create_time ,interval f_delay_for_day day) < #{date},1,0) as f_flag
			from t_reward_withdraw_orders
			where f_status = 0 or f_status = 1
	        ) t where f_flag = 1
		limit #{size}
